import { Link, useLocation } from "react-router-dom";
import React, { useState, useEffect } from "react";
import "../../styles/style.css";
import styled from 'styled-components'

const SectionLab = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
    padding: 25px 0px 20px;
    .section__lab-blockSearch {
        display: flex;
        gap: 10px;
        align-items: center;
    }
    .section__lab-input {
        width: 290px;
        height: 26px;
        padding: 10px;
        background: #F0F0F0;
        border-style: none;
        border-radius: 4px;
        font-size: 16px;
    }
    .section__lab-input:focus {
        outline-width: 0;
    }
    .section__lab-block {
        padding-left: 2%;
    }

    .section__lab-button {
        width: 285px;
        padding: 10px;
        border-radius: 4px;
        text-align: center;
        color: #000;
        font-family: 'Montserrat';
        line-height: 27px;
        text-decoration: none;
        background: #F0F0F0;
        border-style: none;
        border-radius: 4px;
        font-size: 10px;
    }
    .section__lab-btn {
        width: 280px;
        padding: 10px;
        font-size: 16px;
        text-align: center;
        color: #000;
        font-family: Montserrat;
        line-height: 27px;
        text-decoration: none;
        background: #F0F0F0;
        border-style: none;
        border-radius: 4px;
    }
    .section__lab-btn:hover {
        background: #D9D9D9;
        transition: 0.3s;
    }
    .section__lab-list {
        width: 1190px;
        padding: 40px;
        font-size: 18px;
        text-align: left;
        color: #000;
        font-family: Montserrat;
        background: #D8D8D861;
        border-radius: 7px;
        margin-top: 20px;
        display: block;
        border-style: none;
    }
    .section__lab-list-ready {
        width: 1190px;
        padding: 40px;
        font-size: 18px;
        text-align: left;
        color: #000;
        font-family: Montserrat;
        background: #E2EDD08C;
        border-radius: 7px;
        margin-top: 20px;
        display: block;
        border-style: none;
    }
    .section__lab-block {
        margin-bottom: 10px;
    }
    .alternate-color {
        background: rgba(216, 216, 216, 0.38);
    }
`

const NameLab = styled.p`
    color: #000;
    text-align: center;
    font-family: 'Montserrat';
    font-size: 19px;
    padding-left: 5%;
`
const ListLab = styled.ul`
    display: flex;
    flex-direction: column;
    gap: 10px;
    .section__lab-number {
        width: 1246px;
        height: auto;
        background: rgba(226, 237, 208, 0.55);
        border-radius: 10px;
        list-style: none;
        display: flex;
        flex-direction: column;
        padding: 20px;
    }
    .list__link {
        text-decoration: none;
    }
    .task-block {
        display: flex;
        justify-content: space-between;
        padding: 5px 0;
    }
    .task-button {
        padding: 5px 10px;
        background-color: #D9D9D9;
        border: none;
        border-radius: 5px;
        cursor: pointer;
    }
`

const StudLaboratory = () => {
    const [labItems, setLabItems] = useState({ isLoading: true, data: [] });
    const [searchValue, setSearchValue] = useState("");

    const history = useLocation();

    const handleSearchChange = (event) => {
        setSearchValue(event.target.value);
    };

    const handleDeleteClick = (index) => {
        const updatedLabItems = [...labItems.data];
        updatedLabItems.splice(index, 1);
        setLabItems((prev) => ({ ...prev, data: updatedLabItems }));
    };

    useEffect(() => {
        const timeout = setTimeout(() => {
            setLabItems((prev) => ({ ...prev, isLoading: false }));
        }, 5000);
        return () => {
            clearTimeout(timeout);
        };
    }, []);

    useEffect(() => {
        fetch('http://0.0.0.0:8002/tasks', {
            method: 'GET',
            headers: {
                'accept': 'application/json'
            }
        })
            .then((response) => {
                if (response.ok) {
                    return response.json();
                } else {
                    throw new Error("Failed to fetch tasks");
                }
            })
            .then((data) => {
                const tasksArray = Object.keys(data).map((key) => ({
                    id: key,
                    description: data[key].description,
                    tasks: data[key].tasks || []  // предполагаем, что задачи находятся в поле tasks
                }));
                setLabItems({ isLoading: false, data: tasksArray });
            })
            .catch(error => {
                console.error(error.message);
            });
    }, []);

    const getColors = (index) => {
        return index % 2 === 0 ? "section__lab-number" : "section__lab-number alternate-color";
    };

    const handleLabClick = (id) => {
        history.push(`/labaStud/${id}`);
    };

    return (
        <>
            <SectionLab>
                <div className="section__lab-block">
                    <div className="section__lab-blockSearch">
                        <input
                            type="text"
                            placeholder="Поиск"
                            value={searchValue}
                            onChange={handleSearchChange}
                            className="section__lab-input"
                        />
                        <Link id="buttonAdd" className="section__lab-btn">
                            Все лабораторные
                        </Link>
                        <Link id="buttonAdd" className="section__lab-btn">
                            Выполнено
                        </Link>
                        <Link id="buttonAdd" className="section__lab-btn">
                            Не выполнено
                        </Link>
                    </div>
                </div>
                <ListLab>
                    {labItems.isLoading ? (
                        <p>Загрузка...</p>
                    ) : (
                        labItems.data
                            .filter(lab => lab.description.toLowerCase().includes(searchValue.toLowerCase()))
                            .map((labItem, index) => (
                                <li className={getColors(index)} key={labItem.id}>
                                    <NameLab>{labItem.description}</NameLab>
                                    {labItem.tasks.length > 0 ? (
                                        labItem.tasks.map((task, taskIndex) => (
                                            <div className="task-block" key={taskIndex}>
                                                <span>{`Задача ${taskIndex + 1}: ${task}`}</span>
                                                <button
                                                    className="task-button"
                                                    onClick={() => handleLabClick(`${labItem.id}/task${taskIndex + 1}`)}
                                                >
                                                    Перейти
                                                </button>
                                            </div>
                                        ))
                                    ) : (
                                        <button
                                            className="task-button"
                                            onClick={() => handleLabClick(labItem.id)}
                                        >
                                            Перейти
                                        </button>
                                    )}
                                </li>
                            ))
                    )}
                </ListLab>
            </SectionLab>
        </>
    );
}

export default StudLaboratory;
