import React, { useState } from 'react';
import styled from 'styled-components';
import { useParams } from 'react-router-dom';
import FileUploader from './FileUploader'; // Ensure this points to your updated component
import base64 from 'base-64';

// Styles for the page
const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 20px;
  font-family: "Montserrat", sans-serif;
  align-items: center;
`;

const BlockTest = styled.div`
  width: 1248px;
  background-color: #F0F0F0;
  border-radius: 7px;
`;

const BlockTask = styled.div`
  width: 1248px;
  background-color: #D9D9D9;
  padding: 10px;
  border-radius: 7px;
  margin-top: 10px;
  cursor: pointer;
  display: flex;
  flex-direction: column;
`;

const TaskHeader = styled.div`
  padding: 10px;
  background-color: ${({ isOpen }) => (isOpen ? '#D9D9D9' : '#F0F0F0')};
  border-radius: 7px 7px 0 0;
  cursor: pointer;
`;

const TitleText = styled.p`
  font-size: 20px;
  text-align: left;
  width: 1250px;
`;

const Title = styled.h1`
  font-size: 18px;
`;

const Text = styled.p`
  font-size: 16px;
  line-height: 1.5;
`;

const Button = styled.button`
  padding: 10px;
  width: 1100px;
  background-color: white;
  border-style: none;
  border-radius: 7px;
  font-family: "Montserrat";
  font-size: 16px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 10px 0;

  &:hover {
    background: #E2EDD0;
    color: #000;
    transition: 0.3s;
  }
`;

const ToggleContent = styled.div`
  display: ${({ isOpen }) => (isOpen ? 'block' : 'none')};
  background: #F0F0F0;
  padding: 15px;
  border-radius: 0 0 7px 7px;
`;

const LabaStud = () => {
  const [file, setFile] = useState(null);
  const [openTask1, setOpenTask1] = useState(false);
  const [openTask2, setOpenTask2] = useState(false);
  const [openTask3, setOpenTask3] = useState(false);
  const { id } = useParams(); // Get the lab ID

  const handleSubmit = async () => {
    if (file) {
      const reader = new FileReader();
      reader.readAsArrayBuffer(file);
      reader.onload = () => {
        const encodedFile = base64.encode(
          String.fromCharCode(...new Uint8Array(reader.result))
        );
        fetch(`http://0.0.0.0:8002/check/1`, {
          headers: {
            'Content-Type': 'application/json',
          },
          method: 'POST',
          body: JSON.stringify({ file: encodedFile }),
        })
          .then((response) => response.json())
          .then((data) => {
            console.log('Результаты тестов:', data);
          })
          .catch((error) => {
            console.error('Ошибка:', error);
          });
      };
    } else {
      alert('Пожалуйста, выберите файл для загрузки.');
    }
  };

  const handleSaveDraft = () => {
    // Logic for saving draft
    alert('Черновик сохранен!');
  };

  return (
    <Container>
      {/* Title of the lab work */}
      <TitleText>Лабораторная работа №{id}: “Наибольшее из трех чисел”</TitleText>

      {/* Block: Description of tests */}
      <BlockTest>
        <Title>Типы тестов, используемые в данной лабораторной работе:</Title>
        <ul>
          <li>Проверка формулы</li>
          <br />
          <li>Автотесты</li>
          <br />
          <li>Проверка скорости выполнения</li>
          <br />
          <li>Проверка чего-нибудь еще</li>
        </ul>
      </BlockTest>

      {/* Block: Task #1 */}
      <BlockTask>
        <TaskHeader onClick={() => setOpenTask1(!openTask1)} isOpen={openTask1}>
          <Text>Задача №1: Найдите наибольшее из трех целых чисел.</Text>
        </TaskHeader>
        <ToggleContent isOpen={openTask1}>
          <Text>
            Даны три целых числа. Найдите наибольшее из них (программа должна вывести ровно одно целое число).
            <br />Под наибольшим в этой задаче понимается число, которое не меньше, чем любое другое.
          </Text>
          <Text>
            <strong>Формат входных данных:</strong> Вводятся три числа.
          </Text>
          <Text>
            <strong>Формат выходных данных:</strong> Выведите ответ на задачу.
          </Text>
          <Text>
            <strong>Sample Input:</strong>
          </Text>
          <Text>1<br />2<br />3</Text>
          <Text>
            <strong>Sample Output:</strong>
          </Text>
          <Text>3</Text>
          <BlockTask>
            <Title>Ответ в виде файла:</Title>
            <FileUploader file={file} setFile={setFile} />
          </BlockTask>
        </ToggleContent>
      </BlockTask>

      {/* Block: Task #2 */}
      <BlockTask>
        <TaskHeader onClick={() => setOpenTask2(!openTask2)} isOpen={openTask2}>
          <Text>Задача №2: ... (вставьте условие задачи)</Text>
        </TaskHeader>
        <ToggleContent isOpen={openTask2}>
          <Text>
            <strong>Формат входных данных:</strong> ...
          </Text>
          <Text>
            <strong>Формат выходных данных:</strong> ...
          </Text>
          <Text>
            <strong>Sample Input:</strong>
          </Text>
          <Text>...</Text>
          <Text>
            <strong>Sample Output:</strong>
          </Text>
          <Text>...</Text>
        </ToggleContent>
      </BlockTask>

      {/* Block: Task #3 */}
      <BlockTask>
        <TaskHeader onClick={() => setOpenTask3(!openTask3)} isOpen={openTask3}>
          <Text>Задача №3: ... (вставьте условие задачи)</Text>
        </TaskHeader>
        <ToggleContent isOpen={openTask3}>
          <Text>
            <strong>Формат входных данных:</strong> ...
          </Text>
          <Text>
            <strong>Формат выходных данных:</strong> ...
          </Text>
          <Text>
            <strong>Sample Input:</strong>
          </Text>
          <Text>...</Text>
          <Text>
            <strong>Sample Output:</strong>
          </Text>
          <Text>...</Text>
        </ToggleContent>
      </BlockTask>

      {/* Buttons for saving draft and submitting */}
      <BlockTest>
        <Button onClick={handleSaveDraft}>Сохранить черновик</Button>
        <Button onClick={handleSubmit}>Отправить на проверку</Button>
      </BlockTest>
    </Container>
  );
};

export default LabaStud;
