import React, { useState } from 'react';
import styled from 'styled-components';
import { Link, useParams } from 'react-router-dom';
import FileUploader from './Button';
import base64 from 'base-64';

// Стили для страницы
const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 20px;
  font-family: "Montserrat", sans-serif;
  align-items: center;
`;

const BlockTask = styled.div`
  width: 1248px;
  background-color: #D5DEF6;
  padding: 30px;
  border-radius: 7px;
`;

const BlockTest = styled.div`
  width: 1248px;
  background-color: #E2EDD0;
  padding: 30px;
  border-radius: 7px;
`;

const BlockTry = styled.div`
  width: 1248px;
  background-color: #D9D9D9;
  padding: 30px;
  border-radius: 7px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Title = styled.h2`
  font-size: 24px;
  margin-bottom: 10px;
`;

const Text = styled.p`
  font-size: 16px;
  line-height: 1.5;
`;

const Button = styled.button`
  padding: 10px;
  width: 1100px;
  background-color: white;
  border-style: none;
  border-radius: 7px;
  font-family: "Montserrat";
  font-size: 16px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 10px 0;

  &:hover {
    background: #E2EDD0;
    color: #000;
    transition: 0.3s;
  }
`;

const StyledLink = styled(Link)`
  text-decoration: none;  // Убираем подчеркивание
`;

// Компонент страницы с блоками
const LabaStud = () => {
  const [file, setFile] = useState(null);
  const { id } = useParams(); // Получаем id лабораторной

  const handleSubmit = async () => {
    if (file) {
      const reader = new FileReader();
      reader.readAsArrayBuffer(file);
      reader.onload = () => {
        const encodedFile = base64.encode(
          String.fromCharCode(...new Uint8Array(reader.result))
        );
        fetch(`http://0.0.0.0:8002/check/1`, {
          headers: {
            'Content-Type': 'application/json',
          },
          method: 'POST',
          body: JSON.stringify({ file: encodedFile }),
        })
          .then((response) => response.json())
          .then((data) => {
            console.log('Результаты тестов:', data);
          })
          .catch((error) => {
            console.error('Ошибка:', error);
          });
      };
    } else {
      alert('Пожалуйста, выберите файл для загрузки.');
    }
  };

  const handleSaveDraft = () => {
    // Логика сохранения черновика
    alert('Черновик сохранен!');
  };

  return (
    <Container>
      {/* Заголовок лабораторной работы */}
      <Title>Лабораторная работа №{id}: “Наибольшее из трех чисел”</Title>

      {/* Блок: Условие задачи */}
      <BlockTask>
        <Text>
          Даны три целых числа. Найдите наибольшее из них (программа должна вывести ровно одно целое число). Под наибольшим в этой задаче понимается число, которое не меньше, чем любое другое.
        </Text>
        <Text>
          <strong>Формат входных данных:</strong> Вводятся три числа.
        </Text>
        <Text>
          <strong>Формат выходных данных:</strong> Выведите ответ на задачу.
        </Text>
        <Text>
          <strong>Sample Input:</strong>
        </Text>
        <Text>1<br />2<br />3</Text>
        <Text>
          <strong>Sample Output:</strong>
        </Text>
        <Text>3</Text>
      </BlockTask>

      {/* Блок: Описание тестов */}
      <BlockTest>
        <Title>Типы тестов, используемые в данной лабораторной работе:</Title>
        <ul>
          <li>Проверка формулы</li>
          <li>Автотесты</li>
          <li>Проверка скорости выполнения</li>
          <li>Проверка чего-нибудь еще</li>
        </ul>
      </BlockTest>

      {/* Блок: Ответ в виде файла */}
      <BlockTask>
        <Title>Ответ в виде файла:</Title>
        <FileUploader file={file} setFile={setFile} onSubmit={handleSubmit} />
      </BlockTask>

      {/* Блок: Кнопки "Отправить на проверку" и "Сохранить черновик" */}
      <BlockTry>
        <Button onClick={handleSubmit}>Отправить на проверку</Button>
        <Button onClick={handleSaveDraft}>Сохранить черновик</Button>
        <StyledLink to={`/attempts/${id}`}>
          <Button>
            <Text>Мои попытки</Text>
          </Button>
        </StyledLink>
      </BlockTry>
    </Container>
  );
};

export default LabaStud;
