import React, { useState } from 'react';
import styled from 'styled-components';
import { Link, useParams } from 'react-router-dom';
import FileUploader from './Button';
import base64 from 'base-64';

// Стили для страницы
const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 20px;
  font-family: "Montserrat", sans-serif;
  align-items: center;
`;

const BlockTest = styled.div`
  width: 1248px;
  background-color: #E2EDD0;
  padding: 30px;
  border-radius: 7px;
`;

const BlockTask = styled.div`
  width: 1248px;
  background-color: #D5DEF6;
  padding: 20px;
  border-radius: 7px;
  margin-top: 10px;
  cursor: pointer;
`;



const Text = styled.p`
  font-size: 16px;
  line-height: 1.5;
`;

const Button = styled.button`
  padding: 10px;
  width: 1100px;
  background-color: white;
  border-style: none;
  border-radius: 7px;
  font-family: "Montserrat";
  font-size: 16px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 10px 0;

  &:hover {
    background: #E2EDD0;
    color: #000;
    transition: 0.3s;
  }
`;

const ToggleContent = styled.div`
  display: ${({ isOpen }) => (isOpen ? 'block' : 'none')};
  margin-top: 10px;
  background: #D9D9D9;
  padding: 15px;
  border-radius: 7px;
`;

const LabaStud = () => {
  const [file, setFile] = useState(null);
  const [openTask1, setOpenTask1] = useState(false);
  const [openTask2, setOpenTask2] = useState(false);
  const [openTask3, setOpenTask3] = useState(false);
  const { id } = useParams(); // Получаем id лабораторной

  const handleSubmit = async () => {
    if (file) {
      const reader = new FileReader();
      reader.readAsArrayBuffer(file);
      reader.onload = () => {
        const encodedFile = base64.encode(
          String.fromCharCode(...new Uint8Array(reader.result))
        );
        fetch(`http://0.0.0.0:8002/check/1`, {
          headers: {
            'Content-Type': 'application/json',
          },
          method: 'POST',
          body: JSON.stringify({ file: encodedFile }),
        })
          .then((response) => response.json())
          .then((data) => {
            console.log('Результаты тестов:', data);
          })
          .catch((error) => {
            console.error('Ошибка:', error);
          });
      };
    } else {
      alert('Пожалуйста, выберите файл для загрузки.');
    }
  };

  const handleSaveDraft = () => {
    // Логика сохранения черновика
    alert('Черновик сохранен!');
  };

  return (
    <Container>
      {/* Заголовок лабораторной работы */}
      Лабораторная работа №{id}: “Наибольшее из трех чисел”

      {/* Блок: Описание тестов */}
      <BlockTest>
        Типы тестов, используемые в данной лабораторной работе:
        <ul>
          <li>Проверка формулы</li>
          <li>Автотесты</li>
          <li>Проверка скорости выполнения</li>
          <li>Проверка чего-нибудь еще</li>
        </ul>
      </BlockTest>

      {/* Блок: Задача №1 */}
      <BlockTask onClick={() => setOpenTask1(!openTask1)}>
        <Text>Задача №1: Найдите наибольшее из трех целых чисел.</Text>
      </BlockTask>
      <ToggleContent isOpen={openTask1}>
        <Text>
          <strong>Формат входных данных:</strong> Вводятся три числа.
        </Text>
        <Text>
          <strong>Формат выходных данных:</strong> Выведите ответ на задачу.
        </Text>
        <Text>
          <strong>Sample Input:</strong>
        </Text>
        <Text>1<br />2<br />3</Text>
        <Text>
          <strong>Sample Output:</strong>
        </Text>
        <Text>3</Text>
      </ToggleContent>

      {/* Блок: Задача №2 */}
      <BlockTask onClick={() => setOpenTask2(!openTask2)}>
        <Text>Задача №2: ... (вставьте условие задачи)</Text>
      </BlockTask>
      <ToggleContent isOpen={openTask2}>
        <Text>
          <strong>Формат входных данных:</strong> ...
        </Text>
        <Text>
          <strong>Формат выходных данных:</strong> ...
        </Text>
        <Text>
          <strong>Sample Input:</strong>
        </Text>
        <Text>...</Text>
        <Text>
          <strong>Sample Output:</strong>
        </Text>
        <Text>...</Text>
      </ToggleContent>

      {/* Блок: Задача №3 */}
      <BlockTask onClick={() => setOpenTask3(!openTask3)}>
        <Text>Задача №3: ... (вставьте условие задачи)</Text>
      </BlockTask>
      <ToggleContent isOpen={openTask3}>
        <Text>
          <strong>Формат входных данных:</strong> ...
        </Text>
        <Text>
          <strong>Формат выходных данных:</strong> ...
        </Text>
        <Text>
          <strong>Sample Input:</strong>
        </Text>
        <Text>...</Text>
        <Text>
          <strong>Sample Output:</strong>
        </Text>
        <Text>...</Text>
      </ToggleContent>

      {/* Кнопки "Отправить на проверку" и "Сохранить черновик" */}
      <BlockTest>
        <Button onClick={handleSubmit}>Отправить на проверку</Button>
        <Button onClick={handleSaveDraft}>Сохранить черновик</Button>
      </BlockTest>
    </Container>
  );
};

export default LabaStud;
